local PANEL = {}function PANEL:Init()		net.Start( "SendRequestInventoryToServer" )		net.WriteString( "1" )		net.SendToServer()	self:SetSize(630,630)	self:SetPos(260,10)	self.Paint = function(pnl,w,h)		draw.RoundedBox(0,0,0,w,h,Color(84,84,84,255))	end	local test_tab_tab = vgui.Create("DPanelList",self)	test_tab_tab:SetSize(630, 630)	test_tab_tab:SetPos(5, 5)	test_tab_tab:SetSpacing( 5 )	test_tab_tab:EnableHorizontal( true )	test_tab_tab:EnableVerticalScrollbar( true )			net.Receive( "SendTableEntity", function(len, ply)			test_tab_tab:Clear(true)			enttbl = net.ReadTable() //print (table.ToString(enttbl))				for X=1,table.Count(enttbl) do 		for Y=1,table.Count(enttbl[X]) do			if(enttbl[X][Y]!=0)then			//print( k..""..ent:GetClass().." model:"..ent:GetModel())			 			local DPanel1 = vgui.Create('DPanel', test_tab_tab)			DPanel1:SetSize(203, 100)			DPanel1.Paint = function()			//surface.SetDrawColor( 255,255,255,255 )			//surface.DrawRect( 0,0,DPanel1:GetWide(),DPanel1:GetTall() )			draw.RoundedBox( 4, 0, 0, DPanel1:GetWide(), DPanel1:GetTall(), Color( 255, 255, 255, 255 ), true, true, true, true )			draw.RoundedBox( 4, 1, 1, DPanel1:GetWide()-2, DPanel1:GetTall()-2, Color( 159, 159, 159, 255 ), true, true, true, true )						if (enttbl[X][Y].class == "money_printer" or enttbl[X][Y].class == "money_printer_silver") then				draw.DrawText("Денежный принтер","AG_SmallFont",DPanel1:GetWide()-130,DPanel1:GetTall()-98,Color(230,230,230,255))				draw.DrawText("Напечатано "..enttbl[X][Y].money .. " $","AG_SmallFont",DPanel1:GetWide()-130,DPanel1:GetTall()-83,Color(230,230,230,255))				draw.DrawText("Уровень: "..enttbl[X][Y].lvl,"AG_SmallFont",DPanel1:GetWide()-130,DPanel1:GetTall()-68,Color(230,230,230,255))								local hpw = 117				local hp = enttbl[X][Y].damage/100*hpw								surface.SetDrawColor(131,131,131)				surface.DrawRect(DPanel1:GetWide()-127,DPanel1:GetTall()-22,121,18)								surface.SetDrawColor(255-hp/hpw*255,hp/hpw*255,0)				surface.DrawRect(DPanel1:GetWide()-125,DPanel1:GetTall()-20,hp,14)								draw.DrawText(enttbl[X][Y].damage.."%","AG_SmallFont",DPanel1:GetWide()-77,DPanel1:GetTall()-20,Color(230,230,230,255))			end			end						local icon = vgui.Create("SpawnIcon", DPanel1)			local model = enttbl[X][Y].model or AG:GetItem(enttbl[X][Y].id).model			icon:SetModel(model)			icon:SetSize(70,70)			icon:SetPos(5,5)			icon:SetMouseInputEnabled(false)					if (enttbl[X][Y].class == "money_printer" or enttbl[X][Y].class == "money_printer_silver") then			/*local name = vgui.Create("DLabel", DPanel1)			name:SetText("") 			name:SizeToContents()			name:SetPos(75, 5)						local count = vgui.Create("DLabel", DPanel1)			count:SetText("Напечатано "..enttbl[X][Y].money .. " $") 			count:SizeToContents()			count:SetPos(75, 10)						local lvl = vgui.Create("DLabel", DPanel1)			lvl:SetText("Уровень: "..enttbl[X][Y].lvl) 			lvl:SizeToContents()			lvl:SetPos(93, 20)					local hpw = 60			local hp = enttbl[X][Y].damage/100*hpw						if enttbl[X][Y].damage >= 100 then txt = "        "..enttbl[X][Y].damage elseif enttbl[X][Y].damage >10 then txt = "         "..enttbl[X][Y].damage elseif enttbl[X][Y].damage <10 then txt = "          "..enttbl[X][Y].damage end			local desc = vgui.Create("DLabel", DPanel1)			desc:SetText(txt)			desc:SetSize(89,16)			desc:SetPos(89, 35)			desc.Paint = function()			surface.SetDrawColor(13,21,178,255)			surface.DrawRect(0,0,62,16)						surface.SetDrawColor(0,0,0,255)			surface.DrawRect(1,1,60,14)						surface.SetDrawColor(255-hp/hpw*255,hp/hpw*255,0)			surface.DrawRect(1,1,hp,14)			end*/		elseif string.find(string.lower(enttbl[X][Y].class), "cstm_") then			local name = vgui.Create("DLabel", DPanel1)			name:SetText("Магазин: "..enttbl[X][Y].clip) 			name:SizeToContents()			name:SetPos(89, 5)					elseif string.find(string.lower(enttbl[X][Y].class), "spawned_shipment") then		local name = vgui.Create("DLabel", DPanel1)			name:SetText("Осталось: "..enttbl[X][Y].count)  			name:SizeToContents()			name:SetPos(75, 5) 					elseif string.find(string.lower(enttbl[X][Y].class), "item_base") then		local item = AG:GetItem(enttbl[X][Y].id)		local name = vgui.Create("DLabel", DPanel1)			name:SetText(item.name)  			name:SizeToContents()			name:SetPos(75, 5) 				local count = vgui.Create("DLabel", DPanel1)			count:SetText("Осталось: "..enttbl[X][Y].count)  			count:SizeToContents()			count:SetPos(75, 20) 					local desc = vgui.Create("DButton", DPanel1)			desc:SetSize(60, 15)			desc:SetPos(79, 62)			desc:SetText("Описание")			desc.DoClick = function()		local description = vgui.Create( "DFrame" )			description:SetPos( ScrW()*-1, ScrH()/2-150 )			description:SetSize( 400, 300 )			description:SetTitle( "Описание предмета: " .. item.name )			description:SetVisible( true )			description:SetDraggable( false )			description:ShowCloseButton( true )			description:MoveTo( ScrW()/2-200, ScrH()/2-150, .5, 0, .5)			description:MakePopup()			description.btnClose.DoClick = function() description:MoveTo( ScrW()*1, ScrH()/2-150, .5, 0, .5) timer.Create("CloseFrame",1,1,function() description:Close() end) description:SetMouseInputEnabled(false) description:SetKeyboardInputEnabled(false) end			description.Paint = function()		draw.RoundedBox( 8, 0, 0, description:GetWide(), description:GetTall(), Color( 0, 0, 0, 240 ) )	end				local text = vgui.Create("DLabel", description)			text:SetText(item.desc)  			text:SizeToContents()  			text:SetPos(5, 25) 			end		end			//10 x 10 bleat			local drop = vgui.Create("DButton", DPanel1)			drop:SetSize(60, 15)			drop:SetPos(14, 62)			drop:SetText("Выложить")			drop.DoClick = function()				RunConsoleCommand("testspawn" , X,Y)			end		test_tab_tab:AddItem(DPanel1)		end		end		end			end)	//DarkRP.addF4MenuTab("Инвентарь", test_tab_tab)//function test_tab_tab:Update()//vseok xD//end//test_tab_tab:Update()//return test_tab_tabendvgui.Register("AG_Inventory",PANEL,"DPanel")//hook.Add("F4MenuTabs", "addTab", InventoryTab)/*AG = AG or {}function AG.UpdateItemTable()	net.Start( "SendRequestItemsTable" )	net.SendToServer()	net.Receive( "SendItemsTable", function(len, ply)	AG_Items = net.ReadTable()	print("Send Table")	end)endhook.Add("OpenF4Menu","Test",AG.UpdateItemTable)function AG:GetItem(id)	return AG_Items[id]end*/